mapper.select:
select

from security_menu_resource


        and menu_resource_id = #{menuResourceId,jdbcType=INTEGER}


        and permission_id = #{permissionId,jdbcType=INTEGER}


        and menu_name = #{menuName,jdbcType=VARCHAR}


        and menu_url = #{menuUrl,jdbcType=VARCHAR}


        and parent_menu_id = #{parentMenuId,jdbcType=INTEGER}


        and create_date = #{createDate,jdbcType=TIMESTAMP}


        and update_date = #{updateDate,jdbcType=TIMESTAMP}

