select
from security_menu_resource where menu_resource_id in
#{item}

mapper.update:
update security_menu_resource


        permission_id = #{set.permissionId,jdbcType=INTEGER},


        menu_name = #{set.menuName,jdbcType=VARCHAR},


        menu_url = #{set.menuUrl,jdbcType=VARCHAR},


        parent_menu_id = #{set.parentMenuId,jdbcType=INTEGER},


        create_date = #{set.createDate,jdbcType=TIMESTAMP},


        update_date = #{set.updateDate,jdbcType=TIMESTAMP},




        and menu_resource_id = #{where.menuResourceId,jdbcType=INTEGER}


        and permission_id = #{where.permissionId,jdbcType=INTEGER}


        and menu_name = #{where.menuName,jdbcType=VARCHAR}


        and menu_url = #{where.menuUrl,jdbcType=VARCHAR}


        and parent_menu_id = #{where.parentMenuId,jdbcType=INTEGER}


        and create_date = #{where.createDate,jdbcType=TIMESTAMP}


        and update_date = #{where.updateDate,jdbcType=TIMESTAMP}

update security_menu_resource



            when menu_resource_id = #{item.menuResourceId} then #{item.permissionId}




            when menu_resource_id = #{item.menuResourceId} then #{item.menuName}




            when menu_resource_id = #{item.menuResourceId} then #{item.menuUrl}




            when menu_resource_id = #{item.menuResourceId} then #{item.parentMenuId}




            when menu_resource_id = #{item.menuResourceId} then #{item.createDate}




            when menu_resource_id = #{item.menuResourceId} then #{item.updateDate}



where menu_resource_id in

    #{item.menuResourceId,jdbcType=INTEGER}

